INSERT INTO `reference_images` (`id`, `img_url`, `title`, `description`, `rvas`, `fvas`, `infill`, `size`, `date_added`) VALUES
(1, 'allref.jpg', '3D Nyomtatott Termékek', 'Különböző színű és komplexitású 3D nyomtatott termékek. A termékek között találhatóak egyszerű, pár óra alatt kinyomtatható eszközök, emellett bonyolultabb, több elemből álló mechanikus termékek is.<br>\r\nA mozgatható, komplexebb termékek nagyobb sűrűséggel és falvastagsággal vannak nyomtatva, hogy ellenállóbak legyenek, amíg a szoborszerű tárgyaknál ezek az értékek kisebbek.', '0.12', '0.4', '40', '180,60,90', '2020-06-00 00:00:00'),
(2, 'einstein.jpg', 'Einstein Mellszobra', 'Albert Einsteint ábrázoló mellszobor. A termék alacsony rétegvastagsággal van nyomtatva, hogy minél pontosabb és részletesebb terméket kapjunk, emellett alacsony sűrűséggel, mivel a szoborszerű termékek nincsenek kitéve túlzott használatnak.<br>\r\nRemek választás lehet díszként a lakásba.', '0.2', '0.4', '40', '200,100,120', '2020-06-00 00:00:00'),
(3, 'hand.jpg', 'Mechanikus Kéz', 'Több elemből és ujjpercből álló mechanikus kéz. A termék anatómiailag pontosan ábrázolja az emberi kezet. Emellett az ujjpercek tetszés szerint mozgathatók a kívánt pozícióba.<br>\r\nA termék a <a href=\"https://www.zaccord.com/item/product=127\" class=\"blueLink font16\">Zaccordon is megvásárolható</a> és a csomag tartalmazza az összeszereléshez szükséges összes kelléket.', '0.2', '0.4', '40', '170,150,70', '2020-06-00 00:00:00'),
(4, 'tree.jpg', 'Foszforeszkáló Fa', 'Sötétben foszforeszkáló fa. A termék egy különleges filamentből van nyomtatva, ami a sötétben zöld színűen világít, világosban pedig fehér színű, gumis hatású. A Zaccordon igény szerint lehetőség van ilyen filamentből is nyomtatni.', '0.2', '0.4', '40', '130,90,100', '2020-06-00 00:00:00'),
(5, 'woman.jpg', 'Meztelen Női Test', 'Anatómiailag hiteles, meztelen női felsőtest. Ideális választás lehet magányos férfiaknak.<br>\r\nElőzetes 3D szkennelés után a Zaccordon lehetőség van bárkinek a csodaszép testét kinyomtatni, így mindig magunkkal vihetjük a kedvesünk szeretett testét.', '0.2', '0.4', '40', '130,90,100', '2020-06-00 00:00:00'),
(6, 'covidmask.jpg', 'COVID-19 Maszk és Filter', 'COVID-19 Maszk és hozzá tartozó filter a koronavírus járvány idejére. A maszk különböző méretekben kapható és igény szerint lehetőség van flexibilis filamentből nyomtatni a terméket, így sokkal kényelmesebb lesz a viselése. A hozzátartozó filter orvosilag tesztelt és kereskedelmi forgalomban kapható.<br>\r\nA maszk nem esett át semmilyen hivatalos teszten, így csakis saját felelősségre használható. Ennek ellenére nagyságrendekkel megbízhatóbb és biztonságosabb mint a jelenleg forgalomban kapható szellősen szövött textil maszkok.', '0.2', '0.4', '40', '130,90,100', '2020-06-00 00:00:00'),
(7, 'exoskeleton.jpg', 'Exoskeleton', 'Kézre szerelhető exoskeleton. A termék több elemből áll, amit a vásárlónak kell összeszerelnie, viszont csomagban az ehhez szükséges eszközök mind megtalálhatóak. Összeszerelés után azonnal használható és nagyon menőn néz ki.<br>\r\nA termék a <a class=\"blueLink font16\" href=\"https://www.zaccord.com/item/product=126\">Zaccordon is megvásárolható</a> és pár napon belül házhoz is szállítjuk.', '0.2', '0.4', '40', '130,90,100', '2020-06-00 00:00:00'),
(8, 'skull.jpg', 'Emberi Koponya', 'Anatómiailag hiteles emberi koponya. Ideális választás lehet fanatikus orvosoknak vagy biológia tanároknak otthonra.', '0.2', '0.4', '40', '130,90,100', '2020-06-00 00:00:00'),
(9, 'vase.jpg', 'Spirál Váza', 'Modern spirál váza. Nagyszerű választás lehet a lakásba díszként, legfőképp hasonló, letisztult stílusú otthonokba.<br>\r\nA termék a <a class=\"blueLink font16\" href=\"https://www.zaccord.com/item/product=131\">Zaccordon is megvásárolható</a> és néhány nap alatt házhoz is szállítjuk.', '0.2', '0.4', '40', '130,90,100', '2020-06-00 00:00:00');

